import React from "react";
import lang from "../utils/languageConstants";
import { useSelector } from "react-redux";

const GptSearchBar = () => {
  const langKey = useSelector((store) => store.config.lang);

  return (
    <div className="pt-[10%] flex justify-center">
      <form className="w-1/2 bg-black grid grid-cols-12">
        <input
          type="text"
          placeholder={langKey.hindi.gptSearchPlaceholder}
          className="p-4 m-4 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-600 col-span-9"
        />
        <button className="m-4 p-4 bg-red-600 text-white rounded-md col-span-3">
          {langKey.hindi.search}
        </button>
      </form>
    </div>
  );
};

export default GptSearchBar;
