const GptSearch = () => {
  return (
    <div className="relative min-h-screen">
      {/* Background */}
      <img
        src={BG_URL}
        alt="Background"
        className="fixed inset-0 -z-10 h-full w-full object-cover"
      />

      {/* Content */}
      <div className="flex flex-col items-center min-h-screen pt-50 md:pt-5 px-4">
        <GptSearchBar />
        <GptMovieSuggestions />
      </div>
    </div>
  );
};

export default GptSearch;
